import MyEditor from "../components/MyEditor";
import "../assets/css/diagnois.css"
import SpeechButton from "../components/SpeechButton";
import { useRef, useState } from "react";

function DiagnosisPage() {
    const doctorAudioRef = useRef();
    const [transcript, setTranscript] = useState('');

    const handleTranscript = (text) => {
        console.log(text)
    //   setTranscript(text);
        if(doctorAudioRef.current){
            let currentContent = doctorAudioRef.getTextContent();
            doctorAudioRef.setTextContent(currentContent + text);
        }

    };
    const handleRelease = () => {
        console.log(transcript);
      };




    return ( 
        <div className="main">
            <div className="diagnois-page">
                <div className="diagnois-page-left">
                    <MyEditor  showTools={true}></MyEditor>

                    <div className="audio-btns">
                        <SpeechButton onTranscript={handleTranscript} ref={doctorAudioRef}></SpeechButton>
                        {/* <SpeechButton></SpeechButton> */}
                        {/* <span>{`Transcript: ${transcript}`}</span> */}
                    </div>
                </div>

                <div className="diagnois-page-right">
                    <MyEditor showTools={true}></MyEditor>
                </div>

            </div>
        </div>
     );
}

export default DiagnosisPage;
